import React, { useState, useReducer, useEffect } from 'react';
import { BookOpen, Home, Leaf, Twitter, Linkedin, Share2, TrendingUp, Award, Zap, Car, Lightbulb, UtensilsCrossed, Plus, Target, Users, Calendar } from 'lucide-react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

// Initial state for the app with MongoDB simulation structure
const initialState = {
  isAuthenticated: false,
  currentView: 'dashboard', // 'dashboard', 'resources', 'activities'
  authMode: 'idle', // 'idle', 'login', 'register'
  user: {
    id: '',
    email: '',
    points: 0,
    target_kg: 50,
    registered_on: ''
  },
  activities: [],
  weatherData: { condition: 'Sunny', temp: '25Â°C' },
  leaderboard: [
    { name: 'EcoWarrior23', points: 1250, rank: 1 },
    { name: 'GreenThumb', points: 1180, rank: 2 },
    { name: 'You', points: 0, rank: 3 },
    { name: 'NatureLover', points: 950, rank: 4 },
    { name: 'ClimateHero', points: 890, rank: 5 }
  ]
};

// Reducer for state management
const appReducer = (state, action) => {
  switch (action.type) {
    case 'TOGGLE_AUTH':
      return { ...state, isAuthenticated: action.payload };
    case 'SET_AUTH_MODE':
      return { ...state, authMode: action.payload };
    case 'SET_VIEW':
      return { ...state, currentView: action.payload };
    case 'SET_USER':
      return { ...state, user: action.payload };
    case 'ADD_ACTIVITY':
      const newActivity = {
        ...action.payload,
        id: Date.now().toString(),
        timestamp: new Date().toISOString(),
        date: new Date().toLocaleDateString()
      };
      const updatedUser = {
        ...state.user,
        points: state.user.points + action.payload.points
      };
      const updatedLeaderboard = state.leaderboard.map(entry => 
        entry.name === 'You' ? { ...entry, points: updatedUser.points } : entry
      ).sort((a, b) => b.points - a.points).map((entry, index) => ({ ...entry, rank: index + 1 }));

      return { 
        ...state, 
        activities: [...state.activities, newActivity],
        user: updatedUser,
        leaderboard: updatedLeaderboard
      };
    case 'SET_WEATHER':
      return { ...state, weatherData: action.payload };
    case 'SET_GOAL':
      return { ...state, user: { ...state.user, target_kg: action.payload } };
    default:
      return state;
  }
};

