// Reducer for state management
const appReducer = (state, action) => {
  switch (action.type) {
    case 'TOGGLE_AUTH':
      return {
        ...state,
        isAuthenticated: action.payload
      };
    case 'SET_AUTH_MODE':
      return {
        ...state,
        authMode: action.payload
      };
    case 'SET_VIEW':
      return {
        ...state,
        currentView: action.payload
      };
    case 'SET_USER':
      return {
        ...state,
        user: action.payload
      };
    case 'ADD_ACTIVITY':
      const newActivity = {
        ...action.payload,
        id: Date.now().toString(),
        timestamp: new Date().toISOString(),
        date: new Date().toLocaleDateString()
      };
      const updatedUser = {
        ...state.user,
        points: state.user.points + action.payload.points
      };
      const updatedLeaderboard = state.leaderboard.map(entry => entry.name === 'You' ? {
        ...entry,
        points: updatedUser.points
      } : entry).sort((a, b) => b.points - a.points).map((entry, index) => ({
        ...entry,
        rank: index + 1
      }));
      return {
        ...state,
        activities: [...state.activities, newActivity],
        user: updatedUser,
        leaderboard: updatedLeaderboard
      };
    case 'SET_WEATHER':
      return {
        ...state,
        weatherData: action.payload
      };
    case 'SET_GOAL':
      return {
        ...state,
        user: {
          ...state.user,
          target_kg: action.payload
        }
      };
    default:
      return state;
  }
};

